sys_script_include.script:
var SetupIntrospector = Class.create();
SetupIntrospector.prototype = {
    initialize: function() {
    },

	getCurrentSetup: function(){
		var result = {};
		result.integrationUsers = this.getIntegrationUsers();
		result.plugins = this.getRequiredPluginsStatus();
		result.hasEvents = this.getEventsStatus();
		return result;
	},

	getIntegrationUsers: function(){
		var result = {};
		var role = "sn_k16_install.integration";
		result.role = role;
		result.users = [];
		var gr = new GlideRecord("sys_user_has_role");
		gr.addQuery("role.name", role);
		gr.addQuery("state", "active")
		gr.query();
		while(gr.next())
			result.users.push({sys_id: gr.user.sys_id.toString(), username: gr.user.user_name.toString()});

		return result;
	},

	getRequiredPluginsStatus: function(){

		var plugins = [];
		//This allows us to use non-registered dependencies to show in the list
		var requiredPlugins = [{id: "com.glideapp.itom.snac", name: "Event Management"}];

		for(var i=0; i < requiredPlugins.length; i++){
			var ret = GlidePluginManager.isActive(requiredPlugins[i].id);
			plugins.push({name: requiredPlugins[i].name, id: requiredPlugins[i].id, status: ret});
		}


		return plugins;
	},

	getEventsStatus: function(){
		var source = "MyApp";
		var grEvent = new GlideRecord("em_event");
		grEvent.addQuery("source", source);
		grEvent.query();
		if(grEvent.hasNext())
			return true;
		return false;
	},

    type: 'SetupIntrospector'
};
